import { Box, Button } from "@chakra-ui/core";
import { Form, Formik } from "formik";
import { useRouter } from "next/router";
import React from "react";
import { InputField } from "../components/InputField";
import { Layout } from "../components/Layout";
import {useCreateWorkoutMutation} from "../generated/graphql";
import { useIsAuth } from "../utils/useIsAuth";
import { withApollo } from "../utils/withApollo";

const CreatePost: React.FC<{}> = ({}) => {
  const router = useRouter();
  useIsAuth();
  const [createWorkout] = useCreateWorkoutMutation();
  return (
    <Layout variant="small">
      <Formik
        initialValues={{ title: "", text: "" }}
        // onSubmit={async (values) => {
        //   const { errors } = await createWorkout({
        //     variables: { input: values },
        //     update: (cache) => {
        //       cache.evict({ fieldName: "posts:{}" });
        //     },
        //   });
        //   if (!errors) {
        //     router.push("/");
        //   }
        // }}
      >
        {({ isSubmitting }) => (
          <Form>
            <Flex>
            <InputField name="workoutType" placeholder="Workout Type" label="workoutType" />
            <InputField name="workoutDt" placeholder="Workout Date" label="workoutDt" />
            </Flex>
            <Button
              mt={4}
              type="submit"
              isLoading={isSubmitting}
              variantColor="teal"
            >
              create workout
            </Button>
          </Form>
        )}
      </Formik>
    </Layout>
  );
};

export default withApollo({ ssr: false })(CreatePost);
