import {
  IconButton,
  Box,
  Button,
  Flex,
  Select,
  Textarea,
} from "@chakra-ui/core";
import { Field, FieldArray, Form, Formik } from "formik";
import { useRouter } from "next/router";
import React from "react";
import { InputField } from "../components/InputField";
import { Layout } from "../components/Layout";
import { useCreateTemplateMutation, useExoQuery } from "../generated/graphql";
import { useIsAuth } from "../utils/useIsAuth";
import { withApollo } from "../utils/withApollo";
import * as yup from "yup";

const validationSchema = yup.object({
  templateDetails: yup.array().of(
    yup.object({
      exoDetail: yup.array().of(
        yup.object({
          sets: yup
            .number()
            .integer()
            .positive()
            .required("Nb sets is required"),
          reps: yup
            .number()
            .integer()
            .positive()
            .required("Nb reps is required"),
          weight: yup.number().positive().required("Weight is required"),
          grade: yup.number().positive().max(10).required("Grade is required"),
        })
      ),
    })
  ),
});

const CreateTemplate: React.FC<{}> = ({}) => {
  const router = useRouter();
  useIsAuth();
  const [createTemplate] = useCreateTemplateMutation();
  const { data: exo } = useExoQuery();
  const exoItems = exo?.exo?.map((e) => {
    return (
      <option value={e.id} key={e.id}>
        {e.exoDesc}
      </option>
    );
  });
  return (
    <Layout variant="regular">
      <Formik
        initialValues={{
          templateType: "",
          name: "",
          templateDetails: [],
        }}
        validationSchema={validationSchema}
        onSubmit={async (values) => {
          const { errors } = await createTemplate({
            variables: { options: values },
            update: (cache) => {
              cache.evict({ fieldName: "templates:{}" });
            },
          });
          if (!errors) {
            router.push("/");
          }
        }}
      >
        {({ isSubmitting, values }) => (
          <Form>
            <Flex>
              <InputField
                name="templateType"
                placeholder="Template Type"
                label="Template Type"
              />
              <InputField
                name="name"
                placeholder="Template name"
                label="Template name"
              />
            </Flex>
            <FieldArray name="templateDetails">
              {(arrayHelpers) => (
                <div>
                  <Button
                    onClick={() =>
                      arrayHelpers.push({
                        exoId: "",
                        exoDetail: [
                          {
                            sets: "",
                            reps: "",
                            weight: "",
                            grade: "",
                            rest: 0,
                            feedback: "",
                          },
                        ],
                      })
                    }
                  >
                    add exercise
                  </Button>
                  {values.templateDetails.map((unitExo, index) => {
                    return (
                      <Box>
                        <Flex>
                          <Field
                            as={Select}
                            name={`templateDetails.${index}.exoId`}
                            type="select"
                          >
                            {exoItems}
                          </Field>
                          <IconButton
                            as={Button}
                            icon="delete"
                            aria-label="Delete exercise"
                            onClick={() => arrayHelpers.remove(index)}
                          />
                        </Flex>
                        <Flex>
                          <FieldArray name="exoDetail">
                            <div>
                              {unitExo.exoDetail.map(
                                (_unitExoDetail, exoIndex) => {
                                  return (
                                    <Flex>
                                      <InputField
                                        name={`templateDetails.${index}.exoDetail.${exoIndex}.sets`}
                                        placeholder="sets"
                                        type="number"
                                      />
                                      <InputField
                                        name={`templateDetails.${index}.exoDetail.${exoIndex}.reps`}
                                        placeholder="reps"
                                        type="number"
                                      />
                                      <InputField
                                        name={`templateDetails.${index}.exoDetail.${exoIndex}.weight`}
                                        placeholder="weight"
                                        type="number"
                                      />
                                      <InputField
                                        name={`templateDetails.${index}.exoDetail.${exoIndex}.grade`}
                                        placeholder="grade"
                                        type="number"
                                      />
                                      <InputField
                                        name={`templateDetails.${index}.exoDetail.${exoIndex}.rest`}
                                        placeholder="rest"
                                        type="number"
                                      />
                                      <InputField
                                        as={Textarea}
                                        name={`templateDetails.${index}.exoDetail.${exoIndex}.feedback`}
                                        placeholder="feedback"
                                        textarea
                                      />
                                      <IconButton
                                        as={Button}
                                        icon="delete"
                                        aria-label="Delete Exo Detail"
                                        onClick={() => {
                                          unitExo.exoDetail.splice(exoIndex, 1);
                                          arrayHelpers.replace(index, unitExo);
                                        }}
                                      />
                                      <IconButton
                                        as={Button}
                                        icon="add"
                                        aria-label="Add Exo Detail"
                                        onClick={() => {
                                          unitExo.exoDetail.splice(
                                            exoIndex + 1,
                                            0,
                                            {
                                              sets: "",
                                              reps: "",
                                              weight: "",
                                              grade: "",
                                              rest: 0,
                                              feedback: "",
                                            }
                                          );
                                          arrayHelpers.replace(index, unitExo);
                                        }}
                                      />
                                    </Flex>
                                  );
                                }
                              )}
                            </div>
                          </FieldArray>
                        </Flex>
                      </Box>
                    );
                  })}
                </div>
              )}
            </FieldArray>
            <Button
              mt={4}
              type="submit"
              isLoading={isSubmitting}
              variantColor="teal"
            >
              create template
            </Button>
          </Form>
        )}
      </Formik>
    </Layout>
  );
};

export default withApollo({ ssr: false })(CreateTemplate);
