import { Box, Button, Flex, Heading, Link, Stack, Text } from "@chakra-ui/core";
import NextLink from "next/link";
import { useState } from "react";
import { EditDeletePostButtons } from "../components/EditDeletePostButtons";
import { Layout } from "../components/Layout";
import { useMeQuery, useWorkoutsQuery} from "../generated/graphql";
import {isServer} from "../utils/isServer";
import { useRouter } from "next/router";
import { withApollo } from "../utils/withApollo";

const Index = () => {
  const router = useRouter();
  const { data: meData, loading: meLoading } = useMeQuery({
    skip: isServer(),
  });

  const { data, error, loading, fetchMore, variables } = useWorkoutsQuery({
    variables: {
      cursor: null,
    },
    notifyOnNetworkStatusChange: true,
  });

  if(!meData) {
     router.push("/login");
  }

  if (!loading && !data) {
    return (
      <div>
        <div>you got query failed for some reason</div>
        <div>{error?.message}</div>
      </div>
    );
  }

  return (
    <Layout>
      {!data && loading ? (
        <div>loading...</div>
      ) : (
        <Stack spacing={8}>
          {data?.workouts?.workouts.map((p) =>
            !p ? null : (
              <Flex key={p.id} p={5} shadow="md" borderWidth="1px">
                <Box flex={1}>
                  <Text>{p.workoutDt}</Text>
                  <Flex align="center">
                    <Text flex={1} mt={4}>
                      {p.workoutType}
                    </Text>
                  </Flex>
                </Box>
              </Flex>
            )
          )}
        </Stack>
      )}
      {data && data?.workouts?.hasMore ? (
        <Flex>
          <Button
            onClick={() => {
              fetchMore({
                variables: {
                  cursor: 
                    data.workouts?.workouts[data.workouts.workouts.length - 1].workoutDt,
                },
              });
            }}
            isLoading={loading}
            m="auto"
            my={8}
          >
            load more
          </Button>
        </Flex>
      ) : null}
    </Layout>
  );
};

export default withApollo({ ssr: true })(Index);
