import React from "react";
import { Box, Link, Flex, Button, Heading, Text } from "@chakra-ui/core";
import NextLink from "next/link";
import { useMeQuery, useLogoutMutation } from "../generated/graphql";
import { isServer } from "../utils/isServer";
import { useApolloClient } from "@apollo/client";
import { useRouter } from "next/router";

interface NavBarProps {}

const MenuItems = ({ children, nextLink }) => (
  <NextLink href={nextLink}>
    <Text mt={{ base: 4, md: 0 }} mr={6} display="block">
      {children}
    </Text>
  </NextLink>
);

export const NavBar: React.FC<NavBarProps> = ({}) => {
  const [show, setShow] = React.useState(false);
  const handleToggle = () => setShow(!show);
  const [logout, { loading: logoutFetching }] = useLogoutMutation();
  const router = useRouter();
  const apolloClient = useApolloClient();
  const { data, loading } = useMeQuery({
    skip: isServer(),
  });

  let body = null;

  // data is loading
  if (loading) {
    // user not logged in
  } else if (!data?.me) {
    body = (
      <>
        <NextLink href="/login">
          <Link mr={2}>login</Link>
        </NextLink>
        <NextLink href="/register">
          <Link>register</Link>
        </NextLink>
      </>
    );
    // user is logged in
  } else {
    body = (
      // <Flex align="center">
      //   <NextLink href="/create-workout">
      //     <Button as={Link} mr={4}>
      //       create workout
      //     </Button>
      //   </NextLink>
      //   <NextLink href="/template">
      //     <Button as={Link} mr={4}>
      //       template
      //     </Button>
      //   </NextLink>
      //   <NextLink href="/profile">
      //     <Button as={Link} mr={4}>
      //       profile
      //     </Button>
      //   </NextLink>
      //   <Box mr={2}>{data.me.username}</Box>
      //   <Button
      //     onClick={async () => {
      //       await logout();
      //       router.reload();
      //       // await apolloClient.resetStore();
      //     }}
      //     isLoading={logoutFetching}
      //     variant="link"
      //   >
      //     logout
      //   </Button>
      // </Flex>
      <>
        <MenuItems nextLink="/create-workout">Create Workout</MenuItems>
        <MenuItems nextLink="/template">Template</MenuItems>
        <MenuItems nextLink="/profile">Profile</MenuItems>
        <Box mr={2}>{data.me.username}</Box>
        <Button
          onClick={async () => {
            await logout();
            router.reload();
            // await apolloClient.resetStore();
          }}
          isLoading={logoutFetching}
          variant="link"
        >
          logout
        </Button>
      </>
    );
  }

  return (
    // <Flex zIndex={1} position="sticky" top={0} bg="tan" p={4}>
    //   <Flex flex={1} m="auto" align="center" maxW={1200}>
    //     <NextLink href="/">
    //       <Link>
    //         <Heading>TrainV2</Heading>
    //       </Link>
    //     </NextLink>
    //     <Box ml={"auto"}>{body}</Box>
    //   </Flex>
    // </Flex>
    <Flex
      as="nav"
      align="center"
      justify="space-between"
      wrap="wrap"
      padding="1.5rem"
      bg="teal.500"
      color="white"
    >
      <Flex align="center" mr={5}>
        <Heading as="h1" size="lg" letterSpacing={"-.1rem"}>
          Chakra UI
        </Heading>
      </Flex>

      <Box display={{ base: "block", md: "none" }} onClick={handleToggle}>
        <svg
          fill="white"
          width="12px"
          viewBox="0 0 20 20"
          xmlns="http://www.w3.org/2000/svg"
        >
          <title>Menu</title>
          <path d="M0 3h20v2H0V3zm0 6h20v2H0V9zm0 6h20v2H0v-2z" />
        </svg>
      </Box>

      <Box
        display={{ sm: show ? "block" : "none", md: "flex" }}
        width={{ sm: "full", md: "auto" }}
        alignItems="center"
        flexGrow={1}
      >
        {body}
      </Box>

      <Box
        display={{ sm: show ? "block" : "none", md: "block" }}
        mt={{ base: 4, md: 0 }}
      >
        <Button bg="transparent" border="1px">
          Create account
        </Button>
      </Box>
    </Flex>
  );
};
