import { Box, Button, Flex, MenuItem, Select, Textarea } from "@chakra-ui/core";
import { Field, FieldArray, Form, Formik } from "formik";
import { useRouter } from "next/router";
import React from "react";
import { InputField } from "../components/InputField";
import { Layout } from "../components/Layout";
import {useCreateWorkoutMutation, useExoQuery} from "../generated/graphql";
import { useIsAuth } from "../utils/useIsAuth";
import { withApollo } from "../utils/withApollo";

const CreatePost: React.FC<{}> = ({}) => {
  const router = useRouter();
  useIsAuth();
  const [createWorkout] = useCreateWorkoutMutation();
  const {data: exo} = useExoQuery();
  const exoItems = exo?.exo?.map((e) => 
                    {
                        return (<option value={e.id} key={e.id}>{e.exoDesc}</option>)
                    }
                ) ;

  return (
    <Layout variant="small">
      <Formik
        initialValues={{ workoutType: "", workoutDt: "", workoutDetails: []}}
        onSubmit={async (values) => {
        //   const { errors } = await createWorkout({
        //     variables: { input: values },
        //     update: (cache) => {
        //       cache.evict({ fieldName: "posts:{}" });
        //     },
        //   });
        //   if (!errors) {
        //     router.push("/");
        //   }
        console.log(values)
        }}
      >
        {({ isSubmitting }) => (
          <Form>
            <Flex>
            <InputField name="workoutType" placeholder="Workout Type" label="Workout Type" />
            <InputField name="workoutDt" type="date" placeholder="Workout Date" label="Workout Date" />
            </Flex>
            <Field as={Select} name="workoutDetails[0].exoId" type='select'>
				{exoItems}
            </Field>
			<InputField name="workoutDetails[0].exoDetailsInput[0].sets" placeholder="Sets" label ="Sets" type="number" />
			<InputField name="workoutDetails[0].exoDetailsInput[0].reps" placeholder="reps" label ="reps" type="number" />
			<InputField name="workoutDetails[0].exoDetailsInput[0].weight" placeholder="weight" label ="weight" type="number" />
			<InputField name="workoutDetails[0].exoDetailsInput[0].grade" placeholder="grade" label ="grade" type="number" />
			<InputField name="workoutDetails[0].exoDetailsInput[0].rest" placeholder="rest" label ="rest" type="number" />
			<Field as={Textarea} name="workoutDetails[0].exoDetailsInput[0].feedback" placeholder="feedback" label ="feedback" type="number" />
            <Button
              mt={4}
              type="submit"
              isLoading={isSubmitting}
              variantColor="teal"
            >
              create workout
            </Button>
          </Form>
        )}
      </Formik>
    </Layout>
  );
};

export default withApollo({ ssr: false })(CreatePost);
