import React from "react";
import {
  Box,
  Button,
  Flex,
  IconButton,
  Link,
  Select,
  Text,
} from "@chakra-ui/core";
import { useExoQuery } from "../generated/graphql";
import { FieldArray, Field } from "formik";
import { InputField } from "./InputField";

interface BodyFormikProps {
  values: WorkoutInput;
}

export const BodyFormik: React.FC<BodyFormikProps> = ({ values }) => {
  const { data: exo } = useExoQuery();

  const exoItems = exo?.exo?.map((e) => {
    return (
      <option value={e.exoId} key={e.exoId}>
        {e.exoDesc}
      </option>
    );
  });

  return (
    <FieldArray name="workoutDetails">
      {(arrayHelpers) => (
        <div>
          <Button
            onClick={() =>
              arrayHelpers.push({
                exoId: "",
                exoDetail: [
                  {
                    sets: "",
                    reps: "",
                    weight: "",
                    grade: "",
                    rest: 0,
                    feedback: "",
                  },
                ],
              })
            }
          >
            add exercise
          </Button>
          {values.workoutDetails.map((unitExo, index) => {
            return (
              <Box>
                <Flex>
                  <Field
                    as={Select}
                    name={`workoutDetails.${index}.exoId`}
                    type="select"
                    width={{ sm: "100%", md: "50%" }}
                  >
                    {exoItems}
                  </Field>
                  <IconButton
                    as={Button}
                    icon="delete"
                    aria-label="Delete exercise"
                    onClick={() => arrayHelpers.remove(index)}
                  />
                </Flex>
                <Flex>
                  <FieldArray name="exoDetail">
                    <>
                      {unitExo.exoDetail.map((_unitExoDetail, exoIndex) => {
                        return (
                          <Flex>
                            <InputField
                              name={`workoutDetails.${index}.exoDetail.${exoIndex}.sets`}
                              placeholder="sets"
                              label={exoIndex === 0 ? "Sets" : null}
                              type="number"
                            />
                            <InputField
                              name={`workoutDetails.${index}.exoDetail.${exoIndex}.reps`}
                              placeholder="reps"
                              label={exoIndex === 0 ? "Reps" : null}
                              type="number"
                            />
                            <InputField
                              name={`workoutDetails.${index}.exoDetail.${exoIndex}.weight`}
                              placeholder="weight"
                              label={exoIndex === 0 ? "Kg" : null}
                              type="number"
                            />
                            <InputField
                              name={`workoutDetails.${index}.exoDetail.${exoIndex}.grade`}
                              placeholder="grade"
                              label={exoIndex === 0 ? "Grade" : null}
                              type="number"
                            />
                            <InputField
                              name={`workoutDetails.${index}.exoDetail.${exoIndex}.rest`}
                              placeholder="rest"
                              label={exoIndex === 0 ? "Rest" : null}
                              type="number"
                            />
                            <InputField
                              name={`workoutDetails.${index}.exoDetail.${exoIndex}.feedback`}
                              placeholder="feedback"
                              textarea
                            />
                            <IconButton
                              as={Button}
                              icon="delete"
                              aria-label="Delete Exo Detail"
                              onClick={() => {
                                unitExo.exoDetail.splice(exoIndex, 1);
                                arrayHelpers.replace(index, unitExo);
                              }}
                            />
                            <IconButton
                              as={Button}
                              icon="add"
                              aria-label="Add Exo Detail"
                              onClick={() => {
                                unitExo.exoDetail.splice(exoIndex + 1, 0, {
                                  sets: "",
                                  reps: "",
                                  weight: "",
                                  grade: "",
                                  rest: 0,
                                  feedback: "",
                                });
                                arrayHelpers.replace(index, unitExo);
                              }}
                            />
                          </Flex>
                        );
                      })}
                    </>
                  </FieldArray>
                </Flex>
              </Box>
            );
          })}
        </div>
      )}
    </FieldArray>
  );
};
