import {
  Flex,
  Box,
  IconButton,
  Button,
  Heading,
  Link,
  Stack,
  Text,
} from "@chakra-ui/core";
import { DaySection } from "../components/DaySection";
import { Layout } from "../components/Layout";
import { useWorkoutsQuery } from "../generated/graphql";
import { withApollo } from "../utils/withApollo";

const Index = () => {
  const { data, error, loading, fetchMore, variables } = useWorkoutsQuery({
    variables: {
      sens: null,
      cursor: null,
    },
    notifyOnNetworkStatusChange: true,
  });

  if (!loading && !data && error && error?.message !== "not authenticated") {
    return (
      <div>
        <div>you got query failed for some reason</div>
        <div>{error?.message}</div>
      </div>
    );
  }
  return (
    <Layout variant="regular">
      <Flex align="center">
        {data && data?.workouts?.hasMorePrev ? (
          <IconButton
            as={Button}
            icon="arrow-left"
            aria-label="Go Backward"
            onClick={() => {
              fetchMore({
                variables: {
                  sens: -1,
                  cursor: data.workouts?.nextCursorValue,
                },
              });
            }}
            isLoading={loading}
            m="auto"
            my={8}
          />
        ) : null}
        {data ? <Box>Week {data?.workouts?.weekNb}</Box> : null}
        {data && data?.workouts?.hasMoreNext ? (
          <IconButton
            as={Button}
            icon="arrow-right"
            aria-label="Go Forward"
            onClick={() => {
              fetchMore({
                variables: {
                  sens: 1,
                  cursor: data.workouts?.nextCursorValue,
                },
              });
            }}
            isLoading={loading}
            m="auto"
            my={8}
          />
        ) : null}
      </Flex>
      {!data && loading ? (
        <div>loading...</div>
      ) : (
        <Stack spacing={8}>
          <Flex>
            {data?.workouts?.workouts.map((p) =>
              !p ? null : (
                <>
                  <DaySection key={p.id} workout={p} />
                </>
              )
            )}
          </Flex>
        </Stack>
      )}
    </Layout>
  );
};

export default withApollo({ ssr: true })(Index);
