import React from "react";
import { Box, Button, Flex, IconButton, Link, Text} from "@chakra-ui/core";
import NextLink from "next/link";
import { useApolloClient } from "@apollo/client";
import {RegularWorkoutFragment, useDeleteWorkoutMutation, useExoQuery} from "../generated/graphql";

interface DaySectionProps {
  workout: RegularWorkoutFragment;
}

export const DaySection: React.FC<DaySectionProps> = ({
  workout
}) => {

  const apolloClient = useApolloClient();
  const {data: exo} = useExoQuery();
  const [deleteWorkout] = useDeleteWorkoutMutation();

  const exoIdtoExoDesc: Record<number, string> = {};
  exo?.exo?.forEach((e) => {
    exoIdtoExoDesc[e.id] = e.exoDesc;
  });

  return (
    <>
    {workout ? (
      <Box p={5} shadow="md" borderWidth="1px">
     <IconButton as={Button} icon="delete" aria-label="Delete Workout" onClick={async (workout) => {
       const { errors } = await deleteWorkout({ variables: {id: workout.id }});
          if (!errors) {
            await apolloClient.resetStore();
          }
     }} />
          <Text>{workout.workoutDt.slice(0,10)}</Text>
          <Box>
            <Text>
              {workout.workoutType}
            </Text>
            {workout?.workoutDetails?.map((d) =>
             !d ? null : (
                <>
                <Text> {exoIdtoExoDesc[d.exoId]} </Text>
                {d.exoDetail.map((e) => 
                !e ? null : (
                    <Text> {e.sets} / {e.weight} </Text>
                    )
                )}
                </>
                )
            )}
          </Box>
      </Box>
    ) : null
    }
    </>
  )
}
