import { IconButton, Box, Button, Flex, Select, Textarea } from "@chakra-ui/core";
import { Field, FieldArray, Form, Formik } from "formik";
import { useRouter } from "next/router";
import React from "react";
import { InputField } from "../components/InputField";
import { Layout } from "../components/Layout";
import {useCreateWorkoutMutation, useExoQuery} from "../generated/graphql";
import { useIsAuth } from "../utils/useIsAuth";
import { withApollo } from "../utils/withApollo";
import * as yup from 'yup';

const validationSchema = yup.object({
    workoutDt: yup.date().max(new Date(new Date().setDate(new Date().getDate()+1)), 'Can\'t create a workout in the future'),
    workoutDetails: yup.array().of(yup.object({
        exoDetails: yup.array().of(yup.object({
            sets: yup.number().positive().required('Nb sets is required'),
            reps: yup.number().positive().required('Nb reps is required'),
            weight: yup.number().positive().required('Weight is required'),
            grade: yup.number().positive().max(10).required('Grade is required'),
        }))
    }))
});

const CreatePost: React.FC<{}> = ({}) => {
  const router = useRouter();
  useIsAuth();
  const [createWorkout] = useCreateWorkoutMutation();
  const {data: exo} = useExoQuery();
  const exoItems = exo?.exo?.map((e) => 
                    {
                        return (<option value={e.id} key={e.id}>{e.exoDesc}</option>)
                    }
                ) ;

  return (
    <Layout variant="regular">
      <Formik 
        initialValues={{ workoutType: "", workoutDt: "", workoutDetails: []}}
        validationSchema={validationSchema}
        onSubmit={async (values) => {
        //   const { errors } = await createWorkout({
        //     variables: { input: values },
        //     update: (cache) => {
        //       cache.evict({ fieldName: "posts:{}" });
        //     },
        //   });
        //   if (!errors) {
        //     router.push("/");
        //   }
        console.log(values)
        }}
      >
        {({ isSubmitting, values}) => (
          <Form>
            <Flex>
            <InputField name="workoutType" placeholder="Workout Type" label="Workout Type" />
            <InputField name="workoutDt" type="date" placeholder="Workout Date" label="Workout Date" />
            </Flex>
			<FieldArray name="workoutDetails">
                {arrayHelpers => (
                <div>
                <Button onClick={() => arrayHelpers.push({
                    exoId: "",
                    exoDetails : [{sets: "", reps:"", weight:"", grade:"",rest:"",feedback:"",} ],
                })}>add exercise</Button>
                {values.workoutDetails.map((unitExo, index) => {
                    return (
                        <Box>
                        <Flex>
                        <Field as={Select} name={`workoutDetails.${index}.exoId`} type='select'>
                            {exoItems}
                        </Field>
                         <IconButton as={Button} icon="delete" aria-label="Delete exercise" onClick={() => arrayHelpers.remove(index)} />
                         </Flex>
                        <Flex>
                        <FieldArray name="exoDetails">
                            <div>
                            {unitExo.exoDetails.map((_unitExoDetail, exoIndex) => {
                                return (
                                <Flex>
                                <InputField name={`workoutDetails.${index}.exoDetails.${exoIndex}.sets`} placeholder="sets" type="number" />
                                <InputField name={`workoutDetails.${index}.exoDetails.${exoIndex}.reps`} placeholder="reps" type="number" />
                                <InputField name={`workoutDetails.${index}.exoDetails.${exoIndex}.weight`} placeholder="weight" type="number" />
                                <InputField name={`workoutDetails.${index}.exoDetails.${exoIndex}.grade`} placeholder="grade" type="number" />
                                <InputField name={`workoutDetails.${index}.exoDetails.${exoIndex}.rest`} placeholder="rest" type="number" />
                                <InputField as={Textarea} name={`workoutDetails.${index}.exoDetails.${exoIndex}.feedback`} placeholder="feedback" textarea />
                                 <IconButton as={Button} icon="delete" aria-label="Delete Exo Detail" onClick={() => {unitExo.exoDetails.splice(exoIndex, 1)
                                 arrayHelpers.replace(index, unitExo)
                                 }}/>
                                 <IconButton as={Button} icon="add" aria-label="Add Exo Detail" onClick={() => {unitExo.exoDetails.splice(exoIndex+1, 0, {sets: "", reps:"", weight:"", grade:"",rest:"",feedback:"",})
                                 arrayHelpers.replace(index, unitExo)
                                 }}/>
                                </Flex>
                                )})}
                            </div>
                        </FieldArray>
                        </Flex>
                        </Box>
                     )})}
                </div>
                )}
			</FieldArray>
            <Button
              mt={4}
              type="submit"
              isLoading={isSubmitting}
              variantColor="teal"
            >
              create workout
            </Button>
          </Form>
        )}
      </Formik>
    </Layout>
  )
};

export default withApollo({ ssr: false })(CreatePost);
