import React from "react";
import { Box, Button, Flex, IconButton, Link, Text } from "@chakra-ui/core";
import NextLink from "next/link";
import { useApolloClient } from "@apollo/client";
import {
  RegularWorkoutFragment,
  useDeleteWorkoutMutation,
  useExoQuery,
} from "../generated/graphql";

interface DaySectionProps {
  workout: RegularWorkoutFragment;
}

export const DaySection: React.FC<DaySectionProps> = ({ workout }) => {
  const apolloClient = useApolloClient();
  const { data: exo } = useExoQuery();
  const [deleteWorkout] = useDeleteWorkoutMutation();

  const exoIdtoExoDesc: Record<number, string> = {};
  exo?.exo?.forEach((e) => {
    exoIdtoExoDesc[e.exoId] = e.exoDesc;
  });

  return (
    <>
      {workout ? (
        <Flex flex={1} align="center">
          <Box minH={400} p={5} shadow="md" borderWidth="1px">
            <Box mb={auto}>
              <Flex flex={1} m="auto" align="center">
                <Box ml={"auto"}>
                  <Text>{workout.workoutType}</Text>
                </Box>
              </Flex>
              {workout?.workoutDetails?.map((d) =>
                !d ? null : (
                  <>
                    <Text> {exoIdtoExoDesc[d.exoId]} </Text>
                    {d.exoDetail.map((e) =>
                      !e ? null : (
                        <Text>
                          {" "}
                          {e.sets} / {e.weight}{" "}
                        </Text>
                      )
                    )}
                  </>
                )
              )}
            </Box>
            <Box mt={"auto"}>
              <Flex flex={1} m="auto" align="center">
                <Box mr={"auto"}>
                  <IconButton
                    as={Button}
                    icon="delete"
                    aria-label="Delete Workout"
                    onClick={async () => {
                      const { errors } = await deleteWorkout({
                        variables: { id: workout.id },
                      });
                      if (!errors) {
                        await apolloClient.resetStore();
                      }
                    }}
                  />
                </Box>
                <Text fontSize="xs">{workout.workoutDt.slice(0, 10)}</Text>
                <Box ml={"auto"}>
                  <NextLink
                    href="/workout/edit/[id]"
                    as={`/workout/edit/${workout.id}`}
                  >
                    <IconButton
                      as={Link}
                      mr={4}
                      icon="edit"
                      aria-label="Edit workout"
                    />
                  </NextLink>
                </Box>
              </Flex>
            </Box>
          </Box>
        </Flex>
      ) : null}
    </>
  );
};
