import {
  IconButton,
  Box,
  Button,
  Flex,
  Select,
  Textarea,
  FormLabel,
  FormControl,
} from "@chakra-ui/core";
import { Field, FieldArray, Form, Formik } from "formik";
import { useRouter } from "next/router";
import React, { useState } from "react";
import { InputField } from "../components/InputField";
import { Layout } from "../components/Layout";
import {
  useCreateTemplateMutation,
  useEditTemplateMutation,
  useExoQuery,
  useTemplateQuery,
  useTemplatesQuery,
} from "../generated/graphql";
import { useIsAuth } from "../utils/useIsAuth";
import { withApollo } from "../utils/withApollo";
import * as yup from "yup";

const validationSchema = yup.object({
  templateDetails: yup.array().of(
    yup.object({
      exoDetail: yup.array().of(
        yup.object({
          sets: yup
            .number()
            .integer()
            .positive()
            .required("Nb sets is required"),
          reps: yup
            .number()
            .integer()
            .positive()
            .required("Nb reps is required"),
          weight: yup.number().positive().required("Weight is required"),
          grade: yup.number().positive().max(10).required("Grade is required"),
        })
      ),
    })
  ),
});

const CreateTemplate: React.FC<{}> = ({}) => {
  const router = useRouter();
  useIsAuth();
  const [createTemplate] = useCreateTemplateMutation();
  const [editTemplate] = useEditTemplateMutation();
  const { data: templates } = useTemplatesQuery();
  const { data: template } = useTemplateQuery({ variables: { templateId: 0 } });
  const { data: exo } = useExoQuery();
  const [templateFct, setTemplateFct] = useState<"create" | "update">("create");
  const [templateId, setTemplateId] = useState<number>(0);
  const exoItems = exo?.exo?.map((e) => {
    return (
      <option value={e.id} key={e.id}>
        {e.exoDesc}
      </option>
    );
  });
  const templatesName = templates?.templates?.map((t) => {
    return (
      <option value={t.id} key={t.id}>
        {t.name}
      </option>
    );
  });
  let templateDetailsClean: [TemplateDetailInput] = [];
  return (
    <Layout variant="regular">
      <Formik
        initialValues={
          template
            ? {
                templateType: "",
                name: "",
                templateDetails: [],
              }
            : {
                templateType: template?.template?.templateType,
                name: template?.template?.name,
                templateDetails: templateDetailsClean,
              }
        }
        validationSchema={validationSchema}
        onSubmit={async (values) => {
          if (templateFct === "create") {
            await createTemplate({
              variables: { options: values },
              update: (cache) => {
                cache.evict({ fieldName: "templates:{}" });
              },
            });
          } else {
            await editTemplate({
              variables: { id: templateId, options: values },
              update: (cache) => {
                cache.evict({ fieldName: "templates:{}" });
              },
            });
          }
          router.push("/");
        }}
      >
        {({ isSubmitting, values }) => (
          <Form>
            <Flex>
              <InputField
                name="templateType"
                placeholder="Template Type"
                label="Template Type"
              />
              <InputField
                name="name"
                placeholder="Template name"
                label="Template name"
              />
              {templates?.templates?.length !== 0 ? (
                <FormControl>
                  <FormLabel htmlFor="templatesName">Template List</FormLabel>
                  <Field
                    as={Select}
                    name="templatesName"
                    label="Template List"
                    type="select"
                    onChange={(t) => {
                      if (t.target.value == "0") {
                        setTemplateFct("create");
                        setTemplateId(0);
                        templateDetailsClean = [];
                      } else {
                        setTemplateFct("update");
                        setTemplateId(t.target.value);
                        const { data: templateChg } = useTemplateQuery({
                          variables: { templateId: t.target.value },
                        });
                        // templateDetailsClean = templateChg.template.templateDetails.map(
                        //   (td) => {
                        //     return {
                        //       exoId: "" + td.exoId,
                        //       exoDetail: td.exoDetail.map((e) => {
                        //         return {
                        //           sets: e.sets,
                        //           reps: e.reps,
                        //           weight: e.weight,
                        //           grade: e.grade,
                        //           rest: e.rest,
                        //           feedback: e.feedback,
                        //         };
                        //       }),
                        //     };
                        //   }
                        // );
                      }
                    }}
                  >
                    <option value="0" key="0"></option>
                    {templatesName}
                  </Field>
                </FormControl>
              ) : null}
            </Flex>
            <FieldArray name="templateDetails">
              {(arrayHelpers) => (
                <div>
                  <Button
                    onClick={() =>
                      arrayHelpers.push({
                        exoId: "",
                        exoDetail: [
                          {
                            sets: "",
                            reps: "",
                            weight: "",
                            grade: "",
                            rest: 0,
                            feedback: "",
                          },
                        ],
                      })
                    }
                  >
                    add exercise
                  </Button>
                  {values.templateDetails.map((unitExo, index) => {
                    return (
                      <Box>
                        <Flex>
                          <Field
                            as={Select}
                            name={`templateDetails.${index}.exoId`}
                            type="select"
                          >
                            {exoItems}
                          </Field>
                          <IconButton
                            as={Button}
                            icon="delete"
                            aria-label="Delete exercise"
                            onClick={() => arrayHelpers.remove(index)}
                          />
                        </Flex>
                        <Flex>
                          <FieldArray name="exoDetail">
                            <div>
                              {unitExo.exoDetail.map(
                                (_unitExoDetail, exoIndex) => {
                                  return (
                                    <Flex>
                                      <InputField
                                        name={`templateDetails.${index}.exoDetail.${exoIndex}.sets`}
                                        placeholder="sets"
                                        type="number"
                                      />
                                      <InputField
                                        name={`templateDetails.${index}.exoDetail.${exoIndex}.reps`}
                                        placeholder="reps"
                                        type="number"
                                      />
                                      <InputField
                                        name={`templateDetails.${index}.exoDetail.${exoIndex}.weight`}
                                        placeholder="weight"
                                        type="number"
                                      />
                                      <InputField
                                        name={`templateDetails.${index}.exoDetail.${exoIndex}.grade`}
                                        placeholder="grade"
                                        type="number"
                                      />
                                      <InputField
                                        name={`templateDetails.${index}.exoDetail.${exoIndex}.rest`}
                                        placeholder="rest"
                                        type="number"
                                      />
                                      <InputField
                                        as={Textarea}
                                        name={`templateDetails.${index}.exoDetail.${exoIndex}.feedback`}
                                        placeholder="feedback"
                                        textarea
                                      />
                                      <IconButton
                                        as={Button}
                                        icon="delete"
                                        aria-label="Delete Exo Detail"
                                        onClick={() => {
                                          unitExo.exoDetail.splice(exoIndex, 1);
                                          arrayHelpers.replace(index, unitExo);
                                        }}
                                      />
                                      <IconButton
                                        as={Button}
                                        icon="add"
                                        aria-label="Add Exo Detail"
                                        onClick={() => {
                                          unitExo.exoDetail.splice(
                                            exoIndex + 1,
                                            0,
                                            {
                                              sets: "",
                                              reps: "",
                                              weight: "",
                                              grade: "",
                                              rest: 0,
                                              feedback: "",
                                            }
                                          );
                                          arrayHelpers.replace(index, unitExo);
                                        }}
                                      />
                                    </Flex>
                                  );
                                }
                              )}
                            </div>
                          </FieldArray>
                        </Flex>
                      </Box>
                    );
                  })}
                </div>
              )}
            </FieldArray>
            {templateFct === "create" ? (
              <Button
                mt={4}
                type="submit"
                isLoading={isSubmitting}
                variantColor="teal"
              >
                create template
              </Button>
            ) : (
              <Button
                mt={4}
                type="submit"
                isLoading={isSubmitting}
                variantColor="teal"
              >
                update template
              </Button>
            )}
          </Form>
        )}
      </Formik>
    </Layout>
  );
};

export default withApollo({ ssr: false })(CreateTemplate);
