import React from "react";
import { FormControl, FormLabel, Select } from "@chakra-ui/core";
import { useRouter } from "next/router";
import { useApolloClient } from "@apollo/client";
import {
  RegularProfileFragment,
  useChangeProfileMutation,
} from "../generated/graphql";
import * as yup from "yup";
import { Field, Formik } from "formik";
import { InputField } from "./InputField";

interface ChangeProfileProps {
  profile: RegularProfileFragment;
}

const validationSchema = yup.object({
  templateDetails: yup.array().of(
    yup.object({
      exoDetail: yup.array().of(
        yup.object({
          sets: yup
            .number()
            .integer()
            .positive()
            .required("Nb sets is required"),
          reps: yup
            .number()
            .integer()
            .positive()
            .required("Nb reps is required"),
          weight: yup.number().positive().required("Weight is required"),
          grade: yup.number().positive().max(10).required("Grade is required"),
        })
      ),
    })
  ),
  trainingFrequency: yup.number().positive(),
});

export const ChangeProfile: React.FC<ChangeProfileProps> = ({ profile }) => {
  const apolloClient = useApolloClient();
  const router = useRouter();
  const [changeProfile] = useChangeProfileMutation();

  return (
    <>
      <Formik
        initialValues={profile}
        validationSchema={validationSchema}
        onSubmit={async (values) => {
          await changeProfile({ variables: { options: values } });
          await apolloClient.resetStore();
          router.push("/");
        }}
      >
        <FormControl>
          <FormLabel htmlFor="lang_cd">Language</FormLabel>
          <Field as={Select} name="lang_cd" label="Language" type="select">
            <option value="FR" key="FR">
              FR
            </option>
            <option value="EN" key="EN">
              EN
            </option>
          </Field>
        </FormControl>
        <InputField
          name="trainingFrequency"
          placeholder="Training Frequency"
          label="Training Frequency"
          type="number"
        />
        <InputField
          name="trainingType"
          placeholder="Training Type"
          label="Training Type"
        />
        <FormControl>
          <FormLabel htmlFor="sex">Sex</FormLabel>
          <Field as={Select} name="sex" label="Sex" type="select">
            <option value="M" key="M">
              M
            </option>
            <option value="F" key="F">
              F
            </option>
          </Field>
        </FormControl>
      </Formik>
    </>
  );
};
