import {
  IconButton,
  Box,
  Button,
  Flex,
  Select,
  Textarea,
  FormControl,
  FormLabel,
} from "@chakra-ui/core";
import { Field, FieldArray, Form, Formik } from "formik";
import { useRouter } from "next/router";
import React, { useState } from "react";
import { InputField } from "../components/InputField";
import { Layout } from "../components/Layout";
import {
  useCreateWorkoutMutation,
  useExoQuery,
  useTemplatesQuery,
  WorkoutInput,
} from "../generated/graphql";
import { useIsAuth } from "../utils/useIsAuth";
import { withApollo } from "../utils/withApollo";
import * as yup from "yup";

const validationSchema = yup.object({
  workoutDt: yup
    .date()
    .max(
      new Date(new Date().setDate(new Date().getDate())),
      "Can't create a workout in the future"
    ),
  workoutDetails: yup.array().of(
    yup.object({
      exoDetail: yup.array().of(
        yup.object({
          sets: yup
            .number()
            .integer()
            .positive()
            .required("Nb sets is required"),
          reps: yup
            .number()
            .integer()
            .positive()
            .required("Nb reps is required"),
          weight: yup.number().positive().required("Weight is required"),
          grade: yup.number().positive().max(10).required("Grade is required"),
        })
      ),
    })
  ),
});

const CreateWorkout: React.FC<{}> = ({}) => {
  const router = useRouter();
  useIsAuth();
  const [createWorkout] = useCreateWorkoutMutation();
  const { data: templates } = useTemplatesQuery();
  const { data: exo } = useExoQuery();
  const exoItems = exo?.exo?.map((e) => {
    return (
      <option value={e.exoId} key={e.exoId}>
        {e.exoDesc}
      </option>
    );
  });
  const [tpInput, setTpInput] = useState<WorkoutInput>({
    workoutType: "",
    workoutDt: "",
    workoutDesc: "",
    workoutDetails: [],
  });
  const initValueForm: Record<number, WorkoutInput> = {};
  const templatesName = templates?.templates?.map((t) => {
    initValueForm[t.id] = {
      workoutType: t.templateType,
      workoutDt: "",
      workoutDesc: "",
      workoutDetails: t.templateDetails.map((td) => {
        return {
          exoId: "" + td.exoId,
          exoDetail: td.exoDetail.map((e) => {
            return {
              sets: e.sets,
              reps: e.reps,
              weight: e.weight,
              grade: e.grade,
              rest: e.rest,
              feedback: e.feedback,
            };
          }),
        };
      }),
    };

    return (
      <option value={t.id} key={t.id}>
        {t.name}
      </option>
    );
  });
  return (
    <Layout>
      <Box mr={{ sd: "none", md: "15%" }} ml={{ sd: "none", md: "15%" }}>
        <Formik
          enableReinitialize={true}
          initialValues={tpInput}
          validationSchema={validationSchema}
          onSubmit={async (values) => {
            const { errors } = await createWorkout({
              variables: { options: values },
              update: (cache) => {
                cache.evict({ fieldName: "workouts:{}" });
              },
            });
            if (!errors) {
              router.push("/");
            }
          }}
        >
          {({ isSubmitting, values }) => (
            <Form>
              <Flex>
                <InputField
                  name="workoutType"
                  placeholder="Workout Type"
                  label="Workout Type"
                />
                <InputField
                  name="workoutDt"
                  type="date"
                  placeholder="Workout Date"
                  label="Workout Date"
                />
                {templates?.templates?.length !== 0 ? (
                  <FormControl>
                    <FormLabel htmlFor="templatesName">Template List</FormLabel>
                    <Field
                      as={Select}
                      name="templatesName"
                      label="Template List"
                      type="select"
                      onChange={(t) => {
                        if (parseInt(t.target.value, 10) === 0) {
                          setTpInput({
                            workoutType: "",
                            workoutDesc: "",
                            workoutDt: "",
                            workoutDetails: [],
                          });
                        } else {
                          setTpInput(initValueForm[t.target.value]);
                        }
                      }}
                    >
                      <option value="0" key="0"></option>
                      {templatesName}
                    </Field>
                  </FormControl>
                ) : null}
              </Flex>
              <FieldArray name="workoutDetails">
                {(arrayHelpers) => (
                  <div>
                    <Button
                      onClick={() =>
                        arrayHelpers.push({
                          exoId: "",
                          exoDetail: [
                            {
                              sets: "",
                              reps: "",
                              weight: "",
                              grade: "",
                              rest: 0,
                              feedback: "",
                            },
                          ],
                        })
                      }
                    >
                      add exercise
                    </Button>
                    {values.workoutDetails.map((unitExo, index) => {
                      return (
                        <Box>
                          <Flex>
                            <Field
                              as={Select}
                              name={`workoutDetails.${index}.exoId`}
                              type="select"
                            >
                              {exoItems}
                            </Field>
                            <IconButton
                              as={Button}
                              icon="delete"
                              aria-label="Delete exercise"
                              onClick={() => arrayHelpers.remove(index)}
                            />
                          </Flex>
                          <Flex>
                            <FieldArray name="exoDetail">
                              <div>
                                {unitExo.exoDetail.map(
                                  (_unitExoDetail, exoIndex) => {
                                    return (
                                      <Flex>
                                        <InputField
                                          name={`workoutDetails.${index}.exoDetail.${exoIndex}.sets`}
                                          placeholder="sets"
                                          type="number"
                                        />
                                        <InputField
                                          name={`workoutDetails.${index}.exoDetail.${exoIndex}.reps`}
                                          placeholder="reps"
                                          type="number"
                                        />
                                        <InputField
                                          name={`workoutDetails.${index}.exoDetail.${exoIndex}.weight`}
                                          placeholder="weight"
                                          type="number"
                                        />
                                        <InputField
                                          name={`workoutDetails.${index}.exoDetail.${exoIndex}.grade`}
                                          placeholder="grade"
                                          type="number"
                                        />
                                        <InputField
                                          name={`workoutDetails.${index}.exoDetail.${exoIndex}.rest`}
                                          placeholder="rest"
                                          type="number"
                                        />
                                        <InputField
                                          as={Textarea}
                                          name={`workoutDetails.${index}.exoDetail.${exoIndex}.feedback`}
                                          placeholder="feedback"
                                          textarea
                                        />
                                        <IconButton
                                          as={Button}
                                          icon="delete"
                                          aria-label="Delete Exo Detail"
                                          onClick={() => {
                                            unitExo.exoDetail.splice(
                                              exoIndex,
                                              1
                                            );
                                            arrayHelpers.replace(
                                              index,
                                              unitExo
                                            );
                                          }}
                                        />
                                        <IconButton
                                          as={Button}
                                          icon="add"
                                          aria-label="Add Exo Detail"
                                          onClick={() => {
                                            unitExo.exoDetail.splice(
                                              exoIndex + 1,
                                              0,
                                              {
                                                sets: "",
                                                reps: "",
                                                weight: "",
                                                grade: "",
                                                rest: 0,
                                                feedback: "",
                                              }
                                            );
                                            arrayHelpers.replace(
                                              index,
                                              unitExo
                                            );
                                          }}
                                        />
                                      </Flex>
                                    );
                                  }
                                )}
                              </div>
                            </FieldArray>
                          </Flex>
                        </Box>
                      );
                    })}
                  </div>
                )}
              </FieldArray>
              <Button
                mt={4}
                type="submit"
                isLoading={isSubmitting}
                variantColor="teal"
              >
                create workout
              </Button>
            </Form>
          )}
        </Formik>
      </Box>
    </Layout>
  );
};

export default withApollo({ ssr: false })(CreateWorkout);
