import React from "react";
import { Box, Button, Flex, IconButton, Link, Text } from "@chakra-ui/core";
import { RegularWorkoutFragment, useExoQuery } from "../generated/graphql";

interface DaySectionProps {
  workout: RegularWorkoutFragment;
  isFirst: boolean;
  isLast: boolean;
}

export const DaySection: React.FC<DaySectionProps> = ({
  workout,
  isFirst,
  isLast,
}) => {
  const { data: exo } = useExoQuery();

  const exoIdtoExoDesc: Record<number, string> = {};
  exo?.exo?.forEach((e) => {
    exoIdtoExoDesc[e.exoId] = e.exoDesc;
  });

  return (
    <>
      {workout ? (
        <Box
          display={{ sm: "flex", md: "block" }}
          p={5}
          ml={isFirst ? "auto" : { base: 0, md: 4 }}
          minH={{ sm: "auto", md: "300px" }}
          minW={{ sm: "auto", md: "300px" }}
          shadow="md"
          borderWidth="1px"
        >
          <Flex flexDirection={"column"} align="center">
            <Box mb={"auto"}>
              <Flex ml="auto" align="center">
                <Box ml={"auto"}>
                  <Text>{workout.workoutType}</Text>
                </Box>
              </Flex>
              {workout?.workoutDetails?.map((d) =>
                !d ? null : (
                  <>
                    <Text> {exoIdtoExoDesc[d.exoId]} </Text>
                    {d.exoDetail.map((e) =>
                      !e ? null : (
                        <Text>
                          {" "}
                          {e.sets} / {e.weight}{" "}
                        </Text>
                      )
                    )}
                  </>
                )
              )}
            </Box>
          </Flex>
        </Box>
      ) : null}
    </>
  );
};
