import { Box, Button, Flex, IconButton, Select, Textarea } from "@chakra-ui/core";
import { Field, FieldArray, Form, Formik } from "formik";
import { useRouter } from "next/router";
import React from "react";
import { InputField } from "../../../components/InputField";
import { Layout } from "../../../components/Layout";
import {useEditWorkoutMutation, useExoQuery, useWorkoutQuery} from "../../../generated/graphql";
import { useGetIntId } from "../../../utils/useGetIntId";
import { withApollo } from "../../../utils/withApollo";

import * as yup from 'yup';

const validationSchema = yup.object({
    workoutDt: yup.date().max(new Date(new Date().setDate(new Date().getDate())), 'Can\'t create a workout in the future'),
    workoutDetails: yup.array().of(yup.object({
        exoDetail: yup.array().of(yup.object({
            sets: yup.number().integer().positive().required('Nb sets is required'),
            reps: yup.number().integer().positive().required('Nb reps is required'),
            weight: yup.number().positive().required('Weight is required'),
            grade: yup.number().positive().max(10).required('Grade is required'),
        }))
    }))
});

const EditWorkout = ({}) => {
  const router = useRouter();
  const intId = useGetIntId();
  const { data, loading } = useWorkoutQuery({
    skip: intId === -1,
    variables: {
      workoutId: intId,
    },
  });
  const {data: exo} = useExoQuery();
  const exoItems = exo?.exo?.map((e) => 
                    {
                        return (<option value={e.id} key={e.id}>{e.exoDesc}</option>)
                    }
                ) ;
  const [editWorkout] = useEditWorkoutMutation();
  if (loading) {
    return (
      <Layout>
        <div>loading...</div>
      </Layout>
    );
  }

  if (!data?.workout) {
    return (
      <Layout>
        <Box>could not find workout</Box>
      </Layout>
    );
  }
  const workoutDetailsClean = data.workout.workoutDetails.map((w) => {
        return ({ exoId: w.exoId , exoDetail: w.exoDetail })
    });
console.log(workoutDetailsClean)
  return (
    <Layout variant="regular">
      <Formik 
        initialValues={{ workoutType: data.workout.workoutType, workoutDesc: data.workout.workoutDesc, workoutDt: data.workout.workoutDt, workoutDetails: data.workout.workoutDetails}}
        validationSchema={validationSchema}
        onSubmit={async (values) => {
          await editWorkout({
            variables: { id: intId, options: { workoutDt: values.workoutDt,
                                               workoutType: values.workoutType,
                                               workoutDesc: values.workoutDesc,
                                               workoutDetails: workoutDetailsClean
                                            },
          }
          });
          router.back();
        }}
      >
        {({ isSubmitting, values}) => (
          <Form>
            <Flex>
            <InputField name="workoutType" placeholder="Workout Type" label="Workout Type" />
            <InputField name="workoutDt" type="date" placeholder="Workout Date" label="Workout Date" />
            </Flex>
			<FieldArray name="workoutDetails">
                {arrayHelpers => (
                <div>
                <Button onClick={() => arrayHelpers.push({
                    exoId: "",
                    exoDetail : [{sets: "", reps:"", weight:"", grade:"",rest: 0 ,feedback:"",} ],
                })}>add exercise</Button>
                {values.workoutDetails.map((unitExo, index) => {
                    return (
                        <Box>
                        <Flex>
                        <Field as={Select} name={`workoutDetails.${index}.exoId`} type='select'>
                            {exoItems}
                        </Field>
                         <IconButton as={Button} icon="delete" aria-label="Delete exercise" onClick={() => arrayHelpers.remove(index)} />
                         </Flex>
                        <Flex>
                        <FieldArray name="exoDetail">
                            <div>
                            {unitExo.exoDetail.map((_unitExoDetail, exoIndex) => {
                                return (
                                <Flex>
                                <InputField name={`workoutDetails.${index}.exoDetail.${exoIndex}.sets`} placeholder="sets" type="number" />
                                <InputField name={`workoutDetails.${index}.exoDetail.${exoIndex}.reps`} placeholder="reps" type="number" />
                                <InputField name={`workoutDetails.${index}.exoDetail.${exoIndex}.weight`} placeholder="weight" type="number" />
                                <InputField name={`workoutDetails.${index}.exoDetail.${exoIndex}.grade`} placeholder="grade" type="number" />
                                <InputField name={`workoutDetails.${index}.exoDetail.${exoIndex}.rest`} placeholder="rest" type="number" />
                                <InputField as={Textarea} name={`workoutDetails.${index}.exoDetail.${exoIndex}.feedback`} placeholder="feedback" textarea />
                                 <IconButton as={Button} icon="delete" aria-label="Delete Exo Detail" onClick={() => {unitExo.exoDetail.splice(exoIndex, 1)
                                 arrayHelpers.replace(index, unitExo)
                                 }}/>
                                 <IconButton as={Button} icon="add" aria-label="Add Exo Detail" onClick={() => {unitExo.exoDetail.splice(exoIndex+1, 0, {sets: "", reps:"", weight:"", grade:"",rest:0,feedback:"",})
                                 arrayHelpers.replace(index, unitExo)
                                 }}/>
                                </Flex>
                                )})}
                            </div>
                        </FieldArray>
                        </Flex>
                        </Box>
                     )})}
                </div>
                )}
			</FieldArray>
            <Button
              mt={4}
              type="submit"
              isLoading={isSubmitting}
              variantColor="teal"
            >
              create workout
            </Button>
          </Form>
        )}
      </Formik>
    </Layout>
  );
};

export default withApollo({ ssr: false })(EditWorkout);
